import React, { useState } from 'react';
import Square from './Square';

function Board() {
    const [squares, setSquares] = useState(Array(9).fill(null));
    cnost [xNext, setXNext] = useState(true);
    const winner = calculateWinner(squares);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else {
      status = 'Next player: ' + (xNext ? 'X' : 'O');
    }

    function handleClick(i) {
        const newSquares = squares.slice();
        if (calculateWinner(newSquares) || newSquares[i]) {
            return;
        }
        newSquares[i] = xNext ? 'X' : 'O';
        setSquares(newSquares);
        setXNext(!xNext);
    }

    function calculateWinner(squares) {
        const lines = [
          [0, 1, 2],
          [3, 4, 5],
          [6, 7, 8],
          [0, 3, 6],
          [1, 4, 7],
          [2, 5, 8],
          [0, 4, 8],
          [2, 4, 6],
        ];
        for (let i = 0; i < lines.length; i++) {
          const [a, b, c] = lines[i];
          if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
            return squares[a];
          }
        }
        return null;
    }

    return (
    <div>
        <div className="status">{status}</div>
        <div className="board-row">
            <Square num={squares[0]} onClick={() => handleClick(0)} />
            <Square num={squares[1]} onClick={() => handleClick(1)} />
            <Square num={squares[2]} onClick={() => handleClick(2)} />
        </div>
        <div className="board-row">
            <Square num={squares[3]} onClick={() => handleClick(3)} />
            <Square num={squares[4]} onClick={() => handleClick(4)} />
            <Square num={squares[5]} onClick={() => handleClick(5)} />
        </div>
        <div className="board-row">
            <Square num={squares[6]} onClick={() => handleClick(6)} />
            <Square num={squares[7]} onClick={() => handleClick(7)} />
            <Square num={squares[8]} onClick={() => handleClick(8)} />
        </div>
    </div>
  );
}

export default Board;
